function loadImage(imagePath){var protocol,host,url=(window.location.protocol||"http")+"//"+window.location.host+imagePath;return new Promise(res=>{fetch(url).then(r=>r.blob()).then(blob=>{var reader=new FileReader;reader.onload=function(){var b64=reader.result.replace(/^data:.+;base64,/,"");res("data:image/png;base64,"+b64)},reader.readAsDataURL(blob)})})}function setPhotos(){var hero=document.querySelector(".hero"),testimonial=document.querySelector(".testimonial"),introPhoto=document.querySelector(".intro__image");loadImage("/images/lucas.jpg").then((function(res){introPhoto.setAttribute("src",res)})),loadImage("/images/hero.jpg").then((function(res){hero.style.backgroundImage="url("+res+")"})),loadImage("/images/testimonial-bg.jpg").then((function(res){testimonial.style.backgroundImage="url("+res+")"}))}function initIntroSwiper(){setTimeout((function(){Swiper||initIntroSwiper(),new Swiper(".about-swiper",{loop:!1,pagination:{el:".about-swiper-pagination",clickable:!0}}),new Swiper(".testimonial-container",{loop:!0,autoplay:{delay:3e3}})}),100)}function init(){setPhotos(),initIntroSwiper()}window.addEventListener("load",init);